(1597, '2014-03-06 02:58:20', '//IMAGE LOADER.cpp\r\n/* Permission is hereby granted, free of charge, to any person obtaining a copy\r\n * of this software and associated documentation files (the "Software"), to deal\r\n * in the Software without restriction, including without limitation the rights\r\n * to use, copy, modify, merge, publish, distribute, sublicense, and/or sell\r\n * copies of the Software, and to permit persons to whom the Software is\r\n * furnished to do so, subject to the following conditions:\r\n * \r\n * The above notice and this permission notice shall be included in all copies\r\n * or substantial portions of the Software.\r\n * \r\n * THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR\r\n * IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY,\r\n * FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE\r\n * AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER\r\n * LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM,\r\n * OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE\r\n * SOFTWARE.\r\n */\r\n/* File for "Textures" lesson of the OpenGL tutorial on\r\n * www.videotutorialsrock.com\r\n */\r\n\r\n\r\n#include "stdafx.h"\r\n#include <assert.h>\r\n#include <fstream>\r\n\r\n#include "imageloader.h"\r\n\r\nusing namespace std;\r\n\r\nImage::Image(char* ps, int w, int h) : pixels(ps), width(w), height(h) {\r\n	\r\n}\r\n\r\nImage::~Image() {\r\n	delete[] pixels;\r\n}\r\n\r\nnamespace {\r\n	//Converts a four-character array to an integer, using little-endian form\r\n	int toInt(const char* bytes) {\r\n		return (int)(((unsigned char)bytes[3] << 24) |\r\n					 ((unsigned char)bytes[2] << 16) |\r\n					 ((unsigned char)bytes[1] << 8) |\r\n					 (unsigned char)bytes[0]);\r\n	}\r\n	\r\n	//Converts a two-character array to a short, using little-endian form\r\n	short toShort(const char* bytes) {\r\n		return (short)(((unsigned char)bytes[1] << 8) |\r\n					   (unsigned char)bytes[0]);\r\n	}\r\n	\r\n	//Reads the next four bytes as an integer, using little-endian form\r\n	int readInt(ifstream &input) {\r\n		char buffer[4];\r\n		input.read(buffer, 4);\r\n		return toInt(buffer);\r\n	}\r\n	\r\n	//Reads the next two bytes as a short, using little-endian form\r\n	short readShort(ifstream &input) {\r\n		char buffer[2];\r\n		input.read(buffer, 2);\r\n		return toShort(buffer);\r\n	}\r\n	\r\n	//Just like auto_ptr, but for arrays\r\n	template<class T>\r\n	class auto_array {\r\n		private:\r\n			T* array;\r\n			mutable bool isReleased;\r\n		public:\r\n			explicit auto_array(T* array_ = NULL) :\r\n				array(array_), isReleased(false) {\r\n			}\r\n			\r\n			auto_array(const auto_array<T> &aarray) {\r\n				array = aarray.array;\r\n				isReleased = aarray.isReleased;\r\n				aarray.isReleased = true;\r\n			}\r\n			\r\n			~auto_array() {\r\n				if (!isReleased && array != NULL) {\r\n					delete[] array;\r\n				}\r\n			}\r\n			\r\n			T* get() const {\r\n				return array;\r\n			}\r\n			\r\n			T &operator*() const {\r\n				return *array;\r\n			}\r\n			\r\n			void operator=(const auto_array<T> &aarray) {\r\n				if (!isReleased && array != NULL) {\r\n					delete[] array;\r\n				}\r\n				array = aarray.array;\r\n				isReleased = aarray.isReleased;\r\n				aarray.isReleased = true;\r\n			}\r\n			\r\n			T* operator->() const {\r\n				return array;\r\n			}\r\n			\r\n			T* release() {\r\n				isReleased = true;\r\n				return array;\r\n			}\r\n			\r\n			void reset(T* array_ = NULL) {\r\n				if (!isReleased && array != NULL) {\r\n					delete[] array;\r\n				}\r\n				array = array_;\r\n			}\r\n			\r\n			T* operator+(int i) {\r\n				return array + i;\r\n			}\r\n			\r\n			T &operator[](int i) {\r\n				return array[i];\r\n			}\r\n	};\r\n}\r\n\r\nImage* loadBMP(const char* filename) {\r\n	ifstream input;\r\n	input.open(filename, ifstream::binary);\r\n	assert(!input.fail() || !"Could not find file");\r\n	char buffer[2];\r\n	input.read(buffer, 2);\r\n	assert(buffer[0] == ''B'' && buffer[1] == ''M'' || !"Not a bitmap file");\r\n	input.ignore(8);\r\n	int dataOffset = readInt(input);\r\n	\r\n	//Read the header\r\n	int headerSize = readInt(input);\r\n	int width;\r\n	int height;\r\n	switch(headerSize) {\r\n		case 40:\r\n			//V3\r\n			width = readInt(input);\r\n			height = readInt(input);\r\n			input.ignore(2);\r\n			assert(readShort(input) == 24 || !"Image is not 24 bits per pixel");\r\n			assert(readShort(input) == 0 || !"Image is compressed");\r\n			break;\r\n		case 12:\r\n			//OS/2 V1\r\n			width = readShort(input);\r\n			height = readShort(input);\r\n			input.ignore(2);\r\n			assert(readShort(input) == 24 || !"Image is not 24 bits per pixel");\r\n			break;\r\n		case 64:\r\n			//OS/2 V2\r\n			assert(!"Can''t load OS/2 V2 bitmaps");\r\n			break;\r\n		case 108:\r\n			//Windows V4\r\n			assert(!"Can''t load Windows V4 bitmaps");\r\n			break;\r\n		case 124:\r\n			//Windows V5\r\n			assert(!"Can''t load Windows V5 bitmaps");\r\n			break;\r\n		default:\r\n			assert(!"Unknown bitmap format");\r\n	}\r\n	\r\n	//Read the data\r\n	int bytesPerRow = ((width * 3 + 3) / 4) * 4 - (width * 3 % 4);\r\n	int size = bytesPerRow * height;\r\n	auto_array<char> pixels(new char[size]);\r\n	input.seekg(dataOffset, ios_base::beg);\r\n	input.read(pixels.get(), size);\r\n	\r\n	//Get the data into the right format\r\n	auto_array<char> pixels2(new char[width * height * 3]);\r\n	for(int y = 0; y < height; y++) {\r\n		for(int x = 0; x < width; x++) {\r\n			for(int c = 0; c < 3; c++) {\r\n				pixels2[3 * (width * y + x) + c] =\r\n					pixels[bytesPerRow * y + 3 * x + (2 - c)];\r\n			}\r\n		}\r\n	}\r\n	\r\n	input.close();\r\n	return new Image(pixels2.release(), width, height);\r\n}', 'Impact v1.1 - Copy.zip', 1, '/files/Engineering/Mechanical Engineering/Dynamics/Impact Dynamics/Impact v1.1/Source Code', 31182880, 0),
