}\r\n			}\r\n			\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n			for(int j=0; j<NumPts; j++){\r\n				for(int q=0; q<2; q++){\r\n				Impact >> velocity[j][q][i];\r\n				\r\n				}\r\n			}\r\n		}\r\n\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n		    Impact >> TE[i];\r\n			\r\n		}\r\n\r\n			\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n			for(int j=0; j<elementNum; j++){\r\n		Impact >> new_element_length[j][i];\r\n			}\r\n		}\r\n\r\n	}\r\n\r\n\r\n}\r\n\r\nvoid savefile(){\r\n   \r\n	char FileName[30];\r\n    cout << "\\nEnter the name of the file you want to create: ";\r\n\r\n    cin >> FileName;\r\n	\r\n	cout<<"\\nEnter 1 if you would like to save the solution with this file\\n Enter 0 if you only want to save the file:  ";\r\n\r\n	cin >> solutionsave;\r\n\r\n	//cout << "\\nEnter the extension of the file you want to create: ";\r\n\r\n	//cin >>	extension;\r\n\r\n\r\n	strcat(FileName, ".");\r\n	strcat(FileName, "impact");\r\n    ofstream Impact(FileName, ios::out);\r\n	Impact << solutionsave << "\\n";\r\n	Impact << timeStep << "\\n";\r\n	Impact << NumPts << "\\n";\r\n	  Impact << forceNum << "\\n";\r\n		Impact << elementNum << "\\n";\r\n		Impact << totalmass << "\\n";\r\n		Impact <<  spring_stiffness << "\\n";\r\n		Impact <<  spring_stiffness2 << "\\n";\r\n		Impact <<  relaxation << "\\n";\r\n		Impact << dampening << "\\n";\r\n		Impact << constraintNum << "\\n";\r\n		Impact << initialVelocityNum << "\\n";\r\n		\r\n	    \r\n	for(int i=0; i<forceNum; i++){\r\n\r\n		for(int j=0; j<5; j++){\r\n\r\n			Impact << forceArray[i][j];\r\n			Impact << "\\n";\r\n\r\n				}\r\n		\r\n	}\r\n\r\n\r\n	\r\n    for(int i=0; i<NumPts; i++){\r\n\r\n		for(int j=0; j<2; j++){\r\n\r\n			Impact << PointArray[i][j];\r\n			     Impact << "\\n";\r\n				}\r\n		\r\n	}\r\n\r\n	\r\n    for(int i=0; i<elementNum; i++){\r\n\r\n		for(int j=0; j<3; j++){\r\n\r\n			Impact << elementArray[i][j];\r\n		   	Impact << "\\n";\r\n				}\r\n		\r\n	}\r\n\r\n	for(int i=0; i<constraintNum; i++){\r\n		for(int j=0; j<2; j++){\r\n			Impact << constraintArray[i][j];\r\n		   	Impact << "\\n";\r\n	               }\r\n				}\r\n\r\n	for(int i=0; i<initialVelocityNum; i++){\r\n\r\n		for(int j=0; j<4; j++){\r\n\r\n			Impact << initialvelocityArray[i][j];\r\n			Impact << "\\n";\r\n\r\n				}\r\n		\r\n	}\r\n\r\n	\r\n\r\n		if (solutionsave == true){\r\n\r\n			for(int i=0; i<MaxNumTimeSteps; i++){\r\n			for(int j=0; j<NumPts; j++){\r\n				for(int q=0; q<2; q++){\r\n        Impact << newPointArray[j][q][i];\r\n		Impact << "\\n";\r\n				}\r\n			}\r\n			}\r\n			\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n			for(int j=0; j<NumPts; j++){\r\n				for(int q=0; q<2; q++){\r\n				Impact << velocity[j][q][i];\r\n				Impact << "\\n";\r\n				}\r\n			}\r\n		}\r\n\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n		    Impact << TE[i];\r\n			Impact << "\\n";\r\n		}\r\n\r\n			\r\n		for(int i=0; i<MaxNumTimeSteps; i++){\r\n			for(int j=0; j<elementNum; j++){\r\n		Impact << new_element_length[j][i];\r\n		Impact << "\\n";\r\n			}\r\n		}\r\n\r\n	}\r\n\r\n}\r\n\r\n\r\n\r\nvoid dynamicsolve(){\r\n	\r\n		float percent_complete;\r\n     	float KEvelocity[MaxNumPts];\r\n	\r\n	    for (int i=0; i<elementNum; i++){//initialize some stuff\r\n       \r\n     \r\n		int elNumIndex1 = elementArray[i][0];\r\n		int elNumIndex2 = elementArray[i][1];\r\n		float xpos1 = PointArray[elNumIndex1][0];\r\n		float ypos1 = PointArray[elNumIndex1][1];\r\n		float xpos2 = PointArray[elNumIndex2][0];\r\n		float ypos2 = PointArray[elNumIndex2][1];\r\n		\r\n		initial_element_length[i][0] = sqrt((xpos2-xpos1)*(xpos2-xpos1)+(ypos2-ypos1)*(ypos2-ypos1));\r\n		//old_initial_element_length[i] = initial_element_length[i];\r\n		brokenforgood[i][0] = 0; //initialize element break\r\n		maxStretch[i]=0.0000001;\r\n		maxCompress[i]=0.0000001;\r\n		//initial length of elements\r\n	\r\n		elementyield[i][0] = strainyield;\r\n		el_ult_break[i][0] = strainbreak;\r\n	   }\r\n\r\n		\r\n	/*cout<< "The initial length of elements:  ";\r\n\r\n	for (int i=0; i<elementNum; i++){\r\n		cout<< initial_element_length[i];cout<<" ";\r\n	}*/\r\n\r\n	//initializing newPointArray\r\n    //defining the node array for time equal zero.\r\n	for(int u=0; u<NumPts; u++){\r\n	  newPointArray[u][0][0]=PointArray[u][0]; //location of nodes at time 0\r\n	  newPointArray[u][1][0]=PointArray[u][1];\r\n	  }\r\n\r\n	//cout<<"initial point positions: ";\r\n	//for (int q=0; q<NumPts; q++){\r\n	//	cout<< newPointArray[q][0][0];cout<<" ";\r\n	//		cout<< newPointArray[q][1][0];cout<<" ";\r\n	//}\r\n\r\n	//initializing force on node\r\n    //defining the force on node array for time equal zero.\r\n	// force on node r\r\n	for(int r=0; r<NumPts; r++){\r\n	    force_on_node[r][0]=0; //[x magnitude][y magnitude]\r\n	    force_on_node[r][1]=0;\r\n		\r\n		for(int n_force=0; n_force<forceNum; n_force++){\r\n		if (forceArray[n_force][0] == r){\r\n			force_on_node[r][0]=forceArray[n_force][1]*forceArray[n_force][2]; //add applied forces to force_on_node array [x magnitude][y magnitude]\r\n	        force_on_node[r][1]=forceArray[n_force][1]*forceArray[n_force][3];\r\n			\r\n		}\r\n	 }\r\n		\r\n  }\r\n		// initial velocity on node r\r\n	for(int r=0; r<NumPts; r++){\r\n	   initialvelocity_on_node[r][0]=0; //[x magnitude][y magnitude]\r\n	    initialvelocity_on_node[r][1]=0;\r\n		\r\n		for(int ini_velocity=0; ini_velocity<initialVelocityNum; ini_velocity++){\r\n		if (initialvelocityArray[ini_velocity][0] == r){\r\n			 initialvelocity_on_node[r][0]=initialvelocityArray[ini_velocity][1]*initialvelocityArray[ini_velocity][2]; //add applied forces to force_on_node array [x magnitude][y magnitude]\r\n	         initialvelocity_on_node[r][1]=initialvelocityArray[ini_velocity][1]*initialvelocityArray[ini_velocity][3];\r\n\r\n		}\r\n	 }\r\n		\r\n  }\r\n\r\n\r\n	//initialize velocity\r\n	for (int i=0; i<NumPts; i++){\r\n		 velocity[i][0][0]=initialvelocity_on_node[i][0];\r\n		 velocity[i][1][0]=initialvelocity_on_node[i][1];\r\n	}\r\n	//initialize spring force absolute value\r\n	for (int i=0; i<NumPts; i++){\r\n		 spring_force[i]=0;\r\n		}\r\n\r\n   pointmass=totalmass/NumPts;\r\n 	//cout<<pointmass;\r\n\r\nfor (int t=1; t<=MaxNumTimeSteps; t++){ // loop over time array\r\n\r\n\r\n\r\n	/// solve for new x and y positions  newPointArray[][][]\r\n\r\n	KE= 0.0;\r\n	PE= 0.0;\r\n	forcePE = 0.0;\r\n	springPE = 0.0;\r\n		\r\n // Euler method\r\n	for(int r=0; r<NumPts; r++){\r\n\r\n		\r\n		//New Position at next time step\r\n	    velocity[r][0][t] = force_on_node[r][0]*timeStep/pointmass + velocity[r][0][t-1];\r\n\r\n		velocity[r][1][t] = force_on_node[r][1]*timeStep/pointmass + velocity[r][1][t-1];\r\n			\r\n		newPointArray[r][0][t] = newPointArray[r][0][t-1] + timeStep*velocity[r][0][t]; \r\n\r\n		newPointArray[r][1][t] = newPointArray[r][1][t-1] + timeStep*velocity[r][1][t]; \r\n\r\n		\r\n		\r\n	// Apply Constraints\r\n\r\n		for(int p=0; p<constraintNum; p++){\r\n       		if (constraintArray[p][0] == r){\r\n		\r\n		if (constraintArray[p][1] == 1 ||constraintArray[p][1] == 3){		\r\n		\r\n		newPointArray[r][0][t] = PointArray[r][0];\r\n		velocity[r][0][t] = 0;\r\n		}\r\n		if (constraintArray[p][1] == 2 ||constraintArray[p][1] == 3){\r\n		newPointArray[r][1][t]  = PointArray[r][1];\r\n		\r\n		velocity[r][1][t] = 0;\r\n\r\n		}\r\n\r\n		}\r\n		}\r\n		\r\n		\r\n		}\r\n\r\n\r\n\r\n	\r\n	for (int o=0; o<elementNum; o++){// loop for calculating new length of elements\r\n\r\n\r\n		int el2NumIndex1 = elementArray[o][0];\r\n		int el2NumIndex2 = elementArray[o][1];\r\n\r\n\r\n        float x1pos1 = newPointArray[el2NumIndex1][0][t];\r\n		float y1pos1 = newPointArray[el2NumIndex1][1][t];\r\n		float x1pos2 = newPointArray[el2NumIndex2][0][t];\r\n		float y1pos2 = newPointArray[el2NumIndex2][1][t];\r\n\r\n		new_element_length[o][t] = sqrt(((x1pos2-x1pos1)*(x1pos2-x1pos1))+((y1pos2-y1pos1)*(y1pos2-y1pos1)));\r\n		\r\n		//Check to see if the element is breaking or broken already, and keep it broken.\r\n	if ((new_element_length[o][t]-initial_element_length[o][t-