tArray[elNumIndex2][0][t];\r\n		float ypos2 = newPointArray[elNumIndex2][1][t];\r\n			\r\n		element_force_direction[p][0] = (xpos2-xpos1)/sqrt((xpos2-xpos1)*(xpos2-xpos1)+(ypos2-ypos1)*(ypos2-ypos1));\r\n		element_force_direction[p][1] = (ypos2-ypos1)/sqrt((xpos2-xpos1)*(xpos2-xpos1)+(ypos2-ypos1)*(ypos2-ypos1));\r\n			\r\n		\r\n	}\r\n\r\n	\r\n\r\n\r\n	\r\n	for (int e=0; e<elementNum; e++){ //absolute value of element forces\r\n		    if (brokenforgood[e][t] == 1){\r\n			//Broken Spring\r\n		     spring_force[e] =0;\r\n		     }else if((new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]>elementyield[e][t-1] && (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]<el_ult_break[e][t-1]){\r\n				 //Plastic Tension\r\n			        if(elementArray[e][2]==1){\r\n				     spring_force[e] = plastic_stiffness*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+\r\n				     (spring_stiffness*strainyield-plastic_stiffness*strainyield);\r\n				\r\n				   elementyield[e][t] = elementyield[e][t-1] + (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]-spring_force[e]/spring_stiffness;\r\n				   el_ult_break[e][t] = el_ult_break[e][t-1] - (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_force[e]/spring_stiffness;\r\n					initial_element_length[e][t] = spring_stiffness*initial_element_length[e][t-1]/(spring_force[e]-spring_stiffness*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_stiffness);\r\n			        \r\n					}else{\r\n				    spring_force[e] = plastic_stiffness2*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+\r\n				     (spring_stiffness2*strainyield-plastic_stiffness2*strainyield);\r\n				\r\n				   elementyield[e][t] = elementyield[e][t-1] + (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]-spring_force[e]/spring_stiffness2;\r\n				  el_ult_break[e][t] = el_ult_break[e][t-1] - (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_force[e]/spring_stiffness2;\r\n			       initial_element_length[e][t] = spring_stiffness2*initial_element_length[e][t-1]/(spring_force[e]-spring_stiffness2*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_stiffness2);\r\n			        \r\n					}\r\n			}else if((new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]<-elementyield[e][t-1]){\r\n				//Plastic Compression\r\n			        if(elementArray[e][2]==1){\r\n				  spring_force[e] = plastic_stiffness*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+\r\n				     (spring_stiffness*-strainyield-plastic_stiffness*-strainyield);\r\n				\r\n				   elementyield[e][t] = elementyield[e][t-1] - (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_force[e]/spring_stiffness;\r\n				  el_ult_break[e][t] = el_ult_break[e][t-1];\r\n					initial_element_length[e][t] = spring_stiffness*initial_element_length[e][t-1]/(spring_force[e]-spring_stiffness*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_stiffness);\r\n			        \r\n					}else{\r\n					 spring_force[e] = plastic_stiffness2*(new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+\r\n				     (spring_stiffness2*-strainyield-plastic_stiffness2*-strainyield);\r\n				\r\n				   elementyield[e][t] = elementyield[e][t-1] - (new_element_length[e][t]-initial_element_length[e][t-1])/initial_element_length[e][t-1]+spring_force[e]/spring_stiffness2;\r\n				  el_ult_break[e][t] = el_ult_break[e][t-1];\r\n			       initial_element_length[e][t] = spring_stiffness2*initial_element_length[e][t-1]/(spring_force[e]-spring_stiffness2*(new_elemen