						force_on_node[r][1] = -dampening*relative_vel_y[k] + force_on_node[r][1];\r\n\r\n						}\r\n				\r\n			     }\r\n\r\n			}else if (r==elementArray[k][1]){ \r\n				\r\n				force_on_node[r][0] = -element_force_direction[k][0]*spring_force[k]+force_on_node[r][0];//xforce\r\n				force_on_node[r][1] = -element_force_direction[k][1]*spring_force[k]+force_on_node[r][1];//yforce\r\n			\r\n				if (brokenforgood[k][t] == 0 ){\r\n				if (newPointArray[r][0][t]<newPointArray[elementArray[k][0]][0][t]){\r\n					\r\n							force_on_node[r][0] = dampening*relative_vel_x[k] + force_on_node[r][0];\r\n\r\n						}else{\r\n\r\n							force_on_node[r][0] = -dampening*relative_vel_x[k] + force_on_node[r][0];\r\n\r\n						}\r\n\r\n				if (newPointArray[r][1][t]<newPointArray[elementArray[k][0]][1][t]){\r\n\r\n							force_on_node[r][1] = dampening*relative_vel_y[k] + force_on_node[r][1];\r\n\r\n						}else{\r\n\r\n							force_on_node[r][1] = -dampening*relative_vel_y[k] + force_on_node[r][1];\r\n\r\n						}\r\n\r\n				\r\n			}\r\n			\r\n			\r\n			}\r\n  \r\n\r\n		}\r\n\r\n	\r\n\r\n\r\n		velocity[r][0][t] = velocity[r][0][t]*relaxation;\r\n		velocity[r][1][t] = velocity[r][1][t]*relaxation;\r\n	\r\n		\r\n		for(int n_force=0; n_force<forceNum; n_force++){\r\n		//add applied forces to force_on_node array [x magnitude][y magnitude]\r\n			if (forceArray[n_force][0] == r && t<=forceArray[n_force][4]){\r\n			force_on_node[r][0]=forceArray[n_force][1]*forceArray[n_force][2]+force_on_node[r][0]; \r\n	        force_on_node[r][1]=forceArray[n_force][1]*forceArray[n_force][3]+force_on_node[r][1];\r\n\r\n			\r\n			forcePE= forceArray[n_force][1]*forceArray[n_force][2]*(newPointArray[r][0][t] - newPointArray[r][0][0])  + forceArray[n_force][1]*forceArray[n_force][3]*(newPointArray[r][1][t]- newPointArray[r][1][0]);\r\n\r\n			\r\n\r\n		}\r\n	 }\r\n		\r\n}\r\n\r\n	\r\n	for(int r=0; r<NumPts; r++){ //Sum KE over all nodes\r\n\r\n		 KEvelocity[r] = sqrt(velocity[r][0][t]*velocity[r][0][t]+ velocity[r][1][t]*velocity[r][1][t]);\r\n		KE = KE + 0.5*pointmass*( KEvelocity[r]* KEvelocity[r]);\r\n	\r\n	}\r\n\r\n	for (int k=0; k<elementNum; k++){\r\n\r\n		if(elementArray[k][2]==1){\r\n			if (brokenforgood[k][t] == 0 && abs((new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1])<=elementyield[k][t-1]){\r\n	           springPE = springPE +  0.5*spring_stiffness*(((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1])*((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1]));\r\n    //PE of springs.\r\n	       }else if((new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1]>elementyield[k][t-1] && (new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1]<el_ult_break[k][t-1]){\r\n		   springPE = springPE +  0.5*plastic_stiffness*(((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1])*((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1]));\r\n	//PE of springs.\r\n			}else{\r\n				springPE = springPE + 0;\r\n			}\r\n\r\n		}else{\r\n\r\n			if (brokenforgood[k] == 0 && abs((new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1])<=elementyield[k][t-1]){\r\n			 springPE = springPE +  0.5*spring_stiffness2*(((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1])*((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1]));\r\n			}else if((new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1]>elementyield[k][t-1] && (new_element_length[k][t]-initial_element_length[k][t-1])/initial_element_length[k][t-1]<el_ult_break[k][t-1]){\r\n		   springPE = springPE +  0.5*plastic_stiffness2*(((new_element_length[k][t] - initial_element_length[k][t-1])/initial_element_length[k][t-1])*((new_element_length[k][t] - i