r = int(timeanalyze*MaxNumTimeSteps);\r\n		if (timeinteger==0){\r\n			cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Position X:   ";\r\n		cout<<PointArray[nodeanalyze][0];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Position Y:    ";\r\n		cout<<PointArray[nodeanalyze][1];cout<<"\\n";\r\n		}else if (timeinteger>MaxNumTimeSteps){\r\n			timeinteger = MaxNumTimeSteps;\r\n		cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Position X:   ";\r\n		cout<<newPointArray[nodeanalyze][0][timeinteger];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Position Y:    ";\r\n		cout<<newPointArray[nodeanalyze][1][timeinteger];cout<<"\\n";\r\n		}else{\r\n			cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Position X:   ";\r\n		cout<<newPointArray[nodeanalyze][0][timeinteger];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Position Y:    ";\r\n		cout<<newPointArray[nodeanalyze][1][timeinteger];cout<<"\\n";\r\n		}\r\n	}\r\n\r\n	if (dataAnalyze == 2) {\r\n\r\n		cout<<"\\nEnter the the time from zero to "; cout<<MaxNumTimeSteps*timeStep;cout<<" seconds in percent from 0 - 1.  ";\r\n		cin>>timeanalyze;\r\n		cout<<"Enter the node number for it''s velocigy at time ";cout<<timeanalyze*MaxNumTimeSteps*timeStep;cout<<" second. ";\r\n		cin>>nodeanalyze;\r\n		int timeinteger = int(timeanalyze*MaxNumTimeSteps);\r\n\r\n        if (timeinteger==0){\r\n		cout<<"Node "; cout<<nodeanalyze; cout<<" Velocity:    ";\r\n		cout<<sqrt(initialvelocity_on_node[nodeanalyze][0]*initialvelocity_on_node[nodeanalyze][0] + initialvelocity_on_node[nodeanalyze][1]*initialvelocity_on_node[nodeanalyze][1]);cout<<"\\n";\r\n        \r\n		cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Velocity X:   ";\r\n		cout<<initialvelocity_on_node[nodeanalyze][0];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Velocity Y:    ";\r\n		cout<<initialvelocity_on_node[nodeanalyze][1];cout<<"\\n";\r\n		}else if ( timeinteger > MaxNumTimeSteps){\r\n			timeinteger=MaxNumTimeSteps;\r\n			cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Velocity X:   ";\r\n		cout<<velocity[nodeanalyze][0][timeinteger];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Velocity Y:    ";\r\n		cout<<velocity[nodeanalyze][1][timeinteger];cout<<"\\n";\r\n\r\n\r\n		}else{\r\n\r\n			cout<<"\\nNode "; cout<<nodeanalyze; cout<<" Velocity X:   ";\r\n		cout<<velocity[nodeanalyze][0][timeinteger];cout<<"\\n";\r\n	    cout<<"Node "; cout<<nodeanalyze; cout<<" Velocity Y:    ";\r\n		cout<<velocity[nodeanalyze][1][timeinteger];cout<<"\\n";\r\n\r\n\r\n\r\n		}\r\n		\r\n	}\r\n\r\n\r\nif (dataAnalyze == 3) {\r\n\r\n	  \r\n		\r\n		cout<<"\\nEnter the the time from zero to "; cout<<MaxNumTimeSteps*timeStep;cout<<" seconds in percent from 0 - 1.  ";\r\n		cin>>timeanalyze;\r\n		int timeinteger = int(timeanalyze*MaxNumTimeSteps);\r\n		cout<<"The total system energy is "; cout<<TE[timeinteger];\r\n		cout<<"\\n";\r\n	}\r\n}\r\n\r\n\r\nint main(int argc, char** argv){\r\n\r\n	glutInit(&argc, argv);\r\n	glutInitDisplayMode(GLUT_SINGLE | GLUT_RGB | GLUT_DEPTH);\r\n	glutInitWindowSize(600, 600);\r\n	glutInitWindowPosition(400, 100);\r\n    //glutCreateWindow(argv[0]);\r\n	glutCreateWindow("Impact v1.1 Non-Linear");\r\n	\r\n	initRendering();\r\n\r\n\r\n     glutDisplayFunc(display);\r\n	\r\n	\r\n	glutReshapeFunc(resizeWindow); //Handles Window Resize\r\n	glutKeyboardFunc(myKeyboardFunc);\r\n	\r\n	glutMouseFunc(myMouseFunc);\r\n	cout<<"Impact v1.1\\n\\n";\r\n	cout<<"Press q to see the function key list.\\n\\n";\r\n\r\n	\r\n	glutTimerFunc(500, update, 0); //Add a timer\r\n	\r\n	\r\n\r\n\r\n	glutMainLoop();\r\n	\r\n	return 0;					// This line is never reached\r\n}', '', 1, '/files/Engineering/Mechanical Engineering/Dynamics/Impact Dynamics/Impact v1.1/Source Code', 0, 0);
